import React from 'react';
import { useSpring, animated } from '@react-spring/web';
import { useParams, useNavigate } from 'react-router-dom';
import './Lab.css';

const labDetails = [
  { id: 1, title: 'Lab 1: Introduction to Web Development', description: 'Learn HTML, CSS, and JavaScript basics.' },
  { id: 2, title: 'Lab 2: Working with Node.js', description: 'Set up a backend server using Node.js.' },
  { id: 3, title: 'Lab 3: REST APIs', description: 'Build and test RESTful APIs.' },
  { id: 4, title: 'Lab 4: Database Integration', description: 'Work with MongoDB to store and retrieve data.' },
  { id: 5, title: 'Lab 5: Authentication', description: 'Implement user authentication and authorization.' },
  { id: 6, title: 'Lab 6: Frontend Frameworks - Bootstrap', description: 'Design responsive UIs using Bootstrap.' },
  { id: 7, title: 'Lab 7: React Basics', description: 'Build and deploy a React app.' },
];

function Lab() {
  const { id } = useParams();
  const navigate = useNavigate();
  const lab = labDetails[parseInt(id) - 1];

  const animationProps = useSpring({
    opacity: 1,
    transform: 'translateY(0px)',
    from: { opacity: 0, transform: 'translateY(20px)' },
  });

  const handleNavigation = (step) => {
    const nextId = parseInt(id) + step;
    if (nextId >= 1 && nextId <= labDetails.length) {
      navigate(`/lab/${nextId}`);
    }
  };

  return (
    <animated.div style={animationProps} className="lab-container">
      <h2>{lab.title}</h2>
      <p>{lab.description}</p>
      <div className="navigation-buttons">
        {id > 1 && (
          <button onClick={() => handleNavigation(-1)}>Previous</button>
        )}
        {id < labDetails.length && (
          <button onClick={() => handleNavigation(1)}>Next</button>
        )}
      </div>
    </animated.div>
  );
}

export default Lab;
